const tree = $input.first().json.tree || [];

const allowed = ['.ts', '.tsx', '.js', '.jsx', '.md', '.json', '.css', '.html', '.yml', '.yaml'];
const excluded = ['node_modules', '.git', 'dist', 'build', '.next', 'coverage'];

const files = tree.filter(f => {
  if (f.type !== 'blob') return false;
  if (excluded.some(ex => f.path.includes(ex))) return false;
  return allowed.some(ext => f.path.endsWith(ext));
});

return files.map(f => ({ json: { path: f.path, sha: f.sha, url: f.url } }));

const items = $input.all();
const output = [];

for (const item of items) {
  const file = item.json;
  if (!file.content) continue;

  let content = '';
  try {
    content = Buffer.from(file.content, 'base64').toString('utf-8');
  } catch(e) {
    continue;
  }

  const chunkSize = 1500;
  const overlap = 200;
  const chunks = [];

  for (let i = 0; i < content.length; i += chunkSize - overlap) {
    chunks.push(content.slice(i, i + chunkSize));
    if (i + chunkSize >= content.length) break;
  }

  chunks.forEach((chunk, idx) => {
    output.push({
      json: {
        content: chunk,
        filepath: file.path,
        filename: file.path.split('/').pop(),
        chunk_index: idx,
        total_chunks: chunks.length,
        source: 'github',
        repo: 'ibaitelexfree-creator/getxobelaeskola',
        indexed_at: new Date().toISOString()
      }
    });
  });
}

return output;

const items = $input.all();
const output = [];
const tableName = $('Supabase: Una tabla a la vez1').first().json.table_name;

for (const item of items) {
  const rows = Array.isArray(item.json) ? item.json : [item.json];

  rows.forEach((row, idx) => {
    const content = JSON.stringify(row, null, 2);
    output.push({
      json: {
        content: content,
        filepath: `supabase/${tableName}`,
        filename: `${tableName}_row_${idx}`,
        table: tableName,
        row_index: idx,
        source: 'supabase',
        indexed_at: new Date().toISOString()
      }
    });
  });
}

return output;

const item = $input.first().json;

return [{
  json: {
    text: item.content,
    metadata: {
      filepath: item.filepath || '',
      filename: item.filename || '',
      source: item.source || 'unknown',
      table: item.table || '',
      chunk_index: item.chunk_index || 0,
      indexed_at: item.indexed_at || new Date().toISOString()
    }
  }
}];

