const emails = $input.all().map(item => item.json);

const extractedData = emails.map(mail => {
  const cleanBody = mail.textContent
    ?.replace(/(\r\n|\n|\r)/gm, ' ')
    ?.replace(/\s+/g, ' ')
    ?.trim()
    ?.slice(0, 4000);

  return {
    to: mail.envelope?.to?.[0]?.address || '',
    from: mail.envelope?.from?.[0]?.address || '',
    fromName: mail.envelope?.from?.[0]?.name || '',
    date: mail.envelope?.date || '',
    subject: mail.envelope?.subject || '',
    body: cleanBody || '',
    emailId: mail.uid || ''
  };
});

return extractedData;

const data = $getWorkflowStaticData('global');

if (!data.processedEmails) {
  data.processedEmails = {};
}

let skipped = 0;
let processed = 0;
const output = [];

for (const item of items) {
  const uid = item.json.emailId;
  const messageId = item.json.messageId || item.json.subject + item.json.date;

  const uniqueKey = uid || messageId;

  if (!uniqueKey) continue;

  if (data.processedEmails[uniqueKey]) {
    skipped++;
  } else {
    data.processedEmails[uniqueKey] = true;
    processed++;
    output.push(item);
  }
}

data.lastRunStats = {
  processed,
  skipped,
  totalSeen: processed + skipped,
  timestamp: new Date().toISOString()
};

const uids = items
  .map(i => Number(i.json.emailId))
  .filter(n => !isNaN(n));

if (uids.length) {
  const maxUidThisRun = Math.max(...uids);
  data.lastUID = Math.max(maxUidThisRun, data.lastUID || 0);
}

return output;

